    def buscar_pedido(self):

            try:
                self.estadoActualPedido = self.estadoActualCombo.currentText()
                self.idpedido = self.idpedidoText.toPlainText()
                helper = pedidoHelpers.PedidoHelper("",0,"","","","","")
                busqueda = helper.buscar_registro(self.idpedido)
                self.fechaPedidoTxt.setText(busqueda[2])
                self.estadoPedidoTxt.setText(busqueda[3])
                self.fechaRecibidoTxt.setText(busqueda[4])
                self.distribuidorTxt.setText(busqueda[5])
                self.montoTxt.setText(str(busqueda[6]))
                self.formaPagoTxt.setText(busqueda[7])

            except pymysql.Error as err:
                print("Algo salio mal", err)


    def actualizar_pedido(self):
        self.idpedido = self.idpedidoText.toPlainText()
        self.msgError = QtWidgets.QMessageBox()
        self.msgError.setText("¿Seguro que desea modificar el Pedido con el ID {}?".format(self.idpedido))
        self.msgError.setIcon(QtWidgets.QMessageBox.Warning)
        self.msgError.setStandardButtons(QtWidgets.QMessageBox.Ok | QtWidgets.QMessageBox.Cancel)
        self.msgError.setWindowTitle("Confirmacion de Actualización")
        self.msgError.exec_()
        self.estadoActual = self.estadoActualCombo.currentText()
        botonConfirmar = self.msgError.button(QtWidgets.QMessageBox.Ok)
        if(self.msgError.clickedButton() == botonConfirmar):
                try:
                    helper = pedidoHelpers.PedidoHelper("",0,"","","","","")
                    helper.actualizar_registro(self.idpedido,self.estadoActual)
                    print("Estado Actualizado", self.estadoActual)


                except pymysql.Error as err:
                    self.msgError = QtWidgets.QMessageBox()
                    self.msgError.setText("El producto que tratas de buscar no existe")
                    self.msgError.setIcon(QtWidgets.QMessageBox.Error)
                    self.msgError.setWindowTitle("Error De Busqueda")
                    self.msgError.exec_()
                    print("Algo salio mal", err)



